$(document).ready(function() {
    let $pw = $('#password')
    let $t = $('#toggle')

    function setOriginalState() {
        $pw.attr('type', 'password')
        $t.text('اظهر')
    }

    $t.on('click', () => {
        const isPw = $pw.attr('type') === 'password'

        if (isPw) {
            $pw.attr('type', 'text')
            $t.text('اخفي')

            setTimeout(setOriginalState, 5000)
        } else {
            setOriginalState()
        }
    })
})